script:
frappe.ui.form.on("Item", "onload", function(frm) {
    cur_frm.set_query("item_group", function() {
    return {
            "filters": {
                "is_group": "1",
		"parent_item_group": "All Item Groups",
            }
        };
    });
});



frappe.ui.form.on("Item", "onload", function(frm) {
    cur_frm.set_query("lp_item_category", function() {
    return {
            "filters": {
                "is_group": "1",
		"parent_item_group": frm.doc.item_group,
            }
        };
    });
});

frappe.ui.form.on("Item", "onload", function(frm) {
    cur_frm.set_query("lp_item_sub_category", function() {
    return {
            "filters": {
                "is_group": "0",
		"parent_item_group": frm.doc.lp_item_category,
            }
        };
    });
});
